import React  from 'react'
import db from '../../firebase'

class Right extends React.Component {
    constructor(){
        super();
        this.state = {
            Email : '',
            Password :'',
            err:null
        }
    this.handleChange = this.handleChange.bind(this)
    this.handleSubmit = this.handleSubmit.bind(this)    
    }
    handleChange(event){
        const { name , value } = event.target
        this.setState({
            [name] : value
        })
    }
    handleSubmit(){
        db
        .auth()
        .createUserWithEmailAndPassword(this.state.Email, this.state.Password)
        .catch(function(error) {
            this.setState({
                err:error.message
            })
        });
    }
    render (){
        return ( 
            <div>
                <h3 style={{textAlign:'center',color:'white'}}>SIGN UP</h3>
                <div >
                    <form onSubmit={this.handleSubmit} method="POST" className="bg-color--white-opacity-lightest">
                    <div className={this.state.err==null?"hide-alert":"alert alert-danger"}>{this.state.err}</div>
                    <input 
                    name="Email"
                    className="form-control s-form-v3__input"
                    type="email"
                    value={this.state.Email}
                    placeholder="Email"
                    onChange={this.handleChange}
                    style={{height:'3.25rem',margin:'20px 0 20px',border:'none',borderBottom:'0.0625rem solid rgba(255, 255, 255, 0.5)',color:'rgba(255,255,255,0.7)',background:'transparent'}}
                    required
                    />
                    <input
                    name="Password"
                    type="password"
                    className="form-control s-form-v3__input"
                    value={this.state.Password}
                    placeholder="Password"
                    onChange={this.handleChange}
                    required
                    style={{height:'3.25rem',margin:'20px 0 20px',border:'none',borderBottom:'0.0625rem solid rgba(255, 255, 255, 0.5)',color:'rgba(255,255,255,0.7)',background:'transparent'}}
                    />
                    <button 
                    style={{height:'3.25rem',margin:'20px 0 20px',border:'none',color:'rgba(255,255,255,0.7)',background:'transparent'}}
                    className="text-uppercase btn-block white-bg rounded-pill">SIGN UP</button>
                    </form>
                </div>
            </div>
        )
    }
}
export default Right;
