import React from 'react'

class PharmacoTool extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            dci:'',
            ddmt:''
        }
        this.handlchange = this.handlchange.bind(this)
        this.handleSubmit = this.handleSubmit.bind(this)
        this.getReg = this.getReg.bind(this)
    }
    handlchange(event){
        const {name ,value } = event.target
        this.setState({
            [name]:value
        })
    }
    handleSubmit(event){
        event.preventDefault();
        console.log(JSON.stringify(this.state))
    }
    getReg(){
        console.log(this.state.dci)
    }
    render(){
        return(
            <div className={this.props.status?"pharma raised":'hide raised'}>
                <form id="pharmatoolin" onSubmit={this.handleSubmit}>
                    <h5 style={{color:'white',paddingTop:'40px'}}>Interaction</h5>
                    <input 
                    name="dci"
                    type='text'
                    placeholder='   DCI'
                    onChange={this.getReg}
                    /><br></br>
                    <input 
                    type='text'
                    placeholder='   Posology'
                    /><br></br>
                    <input 
                    type='text'
                    placeholder='   Voie'
                    /><br></br>
                    <input 
                    type='number'
                    placeholder='   NÂ°lot'
                    /><br></br>
                    <input 
                    type='date'
                    placeholder='DAEI'
                    /><br></br>
                    <input 
                    type='date'
                    placeholder='DEM'
                    /><br></br>
                    <input 
                    value={this.state.ddmt}
                    name='ddmt'
                    type='date'
                    onChange={this.handlchange}
                    placeholder='DDMT'
                    /><br></br>
                    <button id="btn">Submit</button>
                <h3>{this.state.ddmt}</h3> 
           </form>
            </div>
        )
    }
}
export default PharmacoTool;
