import React  from 'react'
import firebase from '../firebase';
import { Link } from "react-router-dom";

class SignIn extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            email:'',
            password:'',
            err : null
        }
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this)
    }
    handleChange(event){
        const { name ,value } = event.target;
        this.setState({
            [name] : value
        })
    }
    handleSubmit(event){
        event.preventDefault();
        const { email, password } = this.state;
        firebase
        .auth()
        .signInWithEmailAndPassword(email, password)
        .catch(error => { this.setState({err:error.message })
        });
    }
    render(){
        return (
           <div className={this.props.status?"log-in":'hide log-in'}>
               <h3>LOG IN</h3>
               <form onSubmit={this.handleSubmit} action="/profile" method="POST">
                   <div className={this.state.err==null?"hiddn":"alert alert-danger"}>
                       {this.state.err}
                   </div>
                   <input 
                   type="email" 
                   name="email" 
                   value={this.state.eMail}
                   placeholder=" * E-mail"
                   onChange={this.handleChange}
                   required
                   />
                   <input 
                   type="password" 
                   name="password" 
                   value={this.state.Password}
                   placeholder=" * Password"
                   onChange={this.handleChange}                               
                   required
                   />
                   <button id='btn'>LOG IN</button>
               </form>
           </div>
        )
    }
} 

export default SignIn;
