import React from 'react'
import { FaPlus } from 'react-icons/fa'

class PharmacoTool extends React.Component{
    constructor(props){
        super(props);
        this.clickNumber = 0
        this.state = {
            drugs:[{name:" ex : ParacÃ©tamole"}],
            Posology:[{dosage:""}],
            Voie:[{via:""}],
            lot:[{number:""}],
            deai:[{date:""}],
            dem:[{date:""}],
            ddmt:[{date:""}],
            effectsInd:[{effects:""}]
        }
        this.handlchange = this.handlchange.bind(this)
        this.handleSubmit = this.handleSubmit.bind(this)
        this.didDciMatch = this.didDciMatch.bind(this)
        this.addDrug = this.addDrug.bind(this)
        this.showInfo = this.showInfo.bind(this)
    }
    handlchange(event){
        const {name ,value } = event.target
        this.setState({
            [name]:value
        })
        this.didDciMatch(this.state.drugs)
    }
    handleSubmit(event){
        event.preventDefault();
        console.log(JSON.stringify(this.state.drugs))
    }
    addDrug(){
        this.state.drugs.pus({ name: "" });
