import React from 'react'
import { FaPlus } from 'react-icons/fa'

class PharmacoTool extends React.Component{
    constructor(props){
        super(props);
        this.clickNumber = 0
        this.state = {
            drugs:[{name:" ex : ParacÃ©tamole"}],
            Posology:[{dosage:""}],
            Voie:[{via:""}],
            lot:[{number:""}],
            deai:[{date:""}],
            dem:[{date:""}],
            ddmt:[{date:""}],
            effectsInd:[{effects:""}]
        }
        this.handlchange = this.handlchange.bind(this)
        this.handleSubmit = this.handleSubmit.bind(this)
        this.didDciMatch = this.didDciMatch.bind(this)
        this.addDrug = this.addDrug.bind(this)
        this.showInfo = this.showInfo.bind(this)
        this.isFocused=this.isFocused.bind(this)
    }
    handlchange(event){
        const {name ,value } = event.target
        this.setState({
            [name]:value
        })
        this.didDciMatch(this.state.drugs)
    }
    handleSubmit(event){
        event.preventDefault();
        console.log(JSON.stringify(this.state.drugs))
    }
    addDrug(){
        this.state.drugs.push({ name: "" });
        this.state.Posology.push({ dosage: "" });
        this.state.Voie.push({ via: "" });
        this.state.lot.push({ number: "" });
        this.state.dem.push({ date: "" });
        this.state.deai.push({ date: "" });
        this.state.ddmt.push({ date: "" });
        this.setState([...this.state.drugs,this.state.Posology]);
    }

    didDciMatch(mdrugs){
        //console.log(this.state.drugs)
    }
    showInfo(){
        const DAEI = document.getElementsByTagName('input[type==date]')[0]
        console.log(DAEI);
    }
    isFocused(num)
        {
            console.log(num)
            if(num==undefined){
                num = 2;
                let form = document.getElementsByClassName('pharmatoolin')[num]
                form.style.zIndex = 1;
                console.log(num)
            }
            else{
            let form = document.getElementsByClassName('pharmatoolin')[num]
            form.style.zIndex += 1;
            }  
        }
    render(){
        return(
        <div className={this.props.status?"pharma raised":'hide raised'}>
            <section className="meds-name">
                <h4 style={{'paddingTop':20}}>Medicaments</h4>
            {this.state.drugs.map((drug,index) => (
            <div>
            <input
            placeholder="   Name of the Drug"
            required={true}
            className="pharmaInput"
            key={index}
            onFocus={this.isFocused(index)}
            value={drug.name}
            onChange={event => {
              this.state.drugs[index].name = event.target.value;
              this.setState([...this.state.drugs]);
            }}
            />
            <br></br>
            </div>
            ))}
                <br></br>
                <FaPlus 
                className="med-add-button"
                onClick={this.addDrug}
                />
            </section>
            <section className="data-details">
                Informations
            </section>
            <section className="side-effects">
            <h4 style={{'paddingTop':20}}>Effects Indesirables</h4>
                <textarea 
                name="effects"
                type="text"
                required={true}
                placeholder="   Side Effect"
                className="pharmaInput"
                />
                 <button id="btn" onSubmit={this.handleSubmit}>Submit</button>
            </section>
            <section className="meds-info">
                {this.state.drugs.map((drug,i)=>(
                <form className="pharmatoolin" method="POST">
                    <h5 style={{paddingTop:'40px'}}>Informations</h5>
                    <input 
                    type='text'
                    placeholder='   Posology'
                    className="pharmaInput"
                    required={true}
                    />
                    <br></br>
                    <input 
                    type='text'
                    placeholder='   Voie'
                    className="pharmaInput"
                    required={true}
                    />
                    <br></br>
                    <input 
                    type='number'
                    placeholder='   NÂ°lot'
                    className="pharmaInput"
                    />
                    <br></br>
                    <input 
                    type='date'
                    placeholder='DAEI'
                    /><br></br>
                    <input 
                    type='date'
                    placeholder='DEM'
                    /><br></br>
                    <input 
                    value={this.state.ddmt}
                    name='ddmt'
                    type='date'
                    onChange={this.handlchange}
                    placeholder='DDMT'
                    /><br></br>
            </form>
                ))}
        </section>
        </div>
        )
    }
}
export default PharmacoTool;
